import React from 'react'
import {connect} from 'react-redux'
import { Alert } from 'react-bootstrap';
const MyAlert = ({alert}) => 
    alert.msg !== undefined &&
    <Alert  variant={alert.type}>
      {alert.msg}
    </Alert>

const mapStateToProps = state =>({
    alert: state.alert
})

export default connect(mapStateToProps)(MyAlert);
