import React from 'react'
import {connect} from 'react-redux'
const Alert = ({alert}) => {
    alert.msg !== undefined &&
    <div>
        {alert.msg}
    </div>
}

const mapStateToProps = state =>({
    alert: state.alert
})

export default connect(mapStateToProps)(Alert);
