import React from 'react'
import {connect} from 'react-redux'
import './Alert.css'
const Alert = ({alert}) => 
    alert.msg !== undefined &&
    <div >
        {alert.msg}
    </div>

const mapStateToProps = state =>({
    alert: state.alert
})

export default connect(mapStateToProps)(Alert);
