import axios from 'axios';
import {setAlert} from '../actions/alert';

import {
    GET_POSTS,
    CREATE_POST,
    UPDATE_LIKES,
    POST_ERROR
} from './types';


//Create post
export const createPost = (post) => async dispatch => {
    try{
       const res = await axios.post('http://localhost:5000/api/post',post)

       dispatch({
        type: CREATE_POST,
        payload:res.data
    });

    dispatch( 
        setAlert('Post succesfully create','sucess')
    )

    }
    catch(err){
        console.log(err.response)
        dispatch({
            type: POST_ERROR,
            payload: {msg : err.response.statusText, status:err.response.status}
        });
    }   

}


//Create post
export const getPosts = () => async dispatch => {
    try{
      console.log('ab')
       const res = await axios.get('http://localhost:5000/api/post')
       dispatch({
           type: GET_POSTS,
           payload: res.data
       }
        
       )
    }
    catch(err){
        console.log(err.response)
        dispatch({
            type: POST_ERROR,
            payload: {msg : err.response.statusText, status:err.response.status}
        });
    }   

}


//Add like
export const addLike = (postId) => async dispatch => {
    try{
       const res = await axios.post(`http://localhost:5000/api/post/like/${postId}`)
       dispatch({
           type: UPDATE_LIKES,
           payload: {postId,likes:res.data}
       }
        
       )
    }
    catch(err){
        dispatch({
            type: POST_ERROR,
            payload: {msg : err.response.statusText, status:err.response.status}
        });
    }   
}

//Remove like
export const removeLike = (postId) => async dispatch => {
    try{
       const res = await axios.post(`http://localhost:5000/api/post/unlike/${postId}`)
       dispatch({
           type: UPDATE_LIKES,
           payload: {postId,likes:res.data}
       }
        
       )
    }
    catch(err){
        dispatch({
            type: POST_ERROR,
            payload: {msg : err.response.statusText, status:err.response.status}
        });
    }   
}
